import 'package:flutter/material.dart';
import 'package:tix_id/button/menubutton.dart';



class TiketkuPage extends StatefulWidget {
  @override
  _TiketkuPageState createState() => _TiketkuPageState();
}

class _TiketkuPageState extends State<TiketkuPage> {
  int _currentIndex = 3;

  final Color primaryColor = Color.fromARGB(255, 15, 15, 27);

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: primaryColor,
      appBar: AppBar(
        backgroundColor: primaryColor,
        title: Text("Tiketku", style: TextStyle(color: Colors.yellow)),
        centerTitle: true,
      ),
      body: Column(
        children: [
          // Pencarian dan lokasi
          Padding(
            padding: const EdgeInsets.all(8.0),
            child: Row(
              children: [
                Expanded(
                  child: TextField(
                    decoration: InputDecoration(
                      filled: true,
                      fillColor: Color.fromARGB(255, 30, 30, 45),
                      hintText: "Cari di TIX ID",
                      hintStyle: TextStyle(color: Colors.grey),
                      prefixIcon: Icon(Icons.search, color: Colors.grey),
                      border: OutlineInputBorder(
                        borderRadius: BorderRadius.circular(8),
                        borderSide: BorderSide.none,
                      ),
                    ),
                  ),
                ),
                SizedBox(width: 8),
                DropdownButton<String>(
                  dropdownColor: Color.fromARGB(255, 30, 30, 45),
                  value: "Jakarta",
                  icon: Icon(Icons.arrow_drop_down, color: Colors.white),
                  style: TextStyle(color: Colors.white),
                  underline: SizedBox(),
                  onChanged: (value) {},
                  items: ["Jakarta", "Surabaya", "Bandung"]
                      .map((city) => DropdownMenuItem(
                            value: city,
                            child: Text(city),
                          ))
                      .toList(),
                ),
              ],
            ),
          ),

          // Daftar tiket
          Expanded(
            child: ListView(
              padding: EdgeInsets.all(8),
              children: [
                TiketCard(
                  image: "data:image/jpeg;base64,",
                  title: "GLADIATOR II",
                  category: "Action, Adventure",
                  rating: "9.5",
                  age:"17+"
                  
                ),
                TiketCard(
                  image: "data:image/jpeg;base64,",
                  title: "RED ONE",
                  category: "Action, Adventure",
                  rating: "9.3",
                  age: "17+",
                ),
              ],
            ),
          ),
        ],
      ),
     
      bottomNavigationBar: MenuBottom(3),
    );
  }
}

class TiketCard extends StatelessWidget {
  final String image;
  final String title;
  final String category;
  final String rating;
  final String age;

  const TiketCard({
    required this.image,
    required this.title,
    required this.category,
    required this.rating,
    required this.age,
  });

  @override
  Widget build(BuildContext context) {
    return Container(
      margin: EdgeInsets.symmetric(vertical: 8),
      decoration: BoxDecoration(
        color: Color.fromARGB(255, 30, 30, 45),
        borderRadius: BorderRadius.circular(8),
        boxShadow: [
          BoxShadow(
            color: Colors.black.withOpacity(0.5),
            blurRadius: 4,
            offset: Offset(2, 2),
          ),
        ],
      ),
      child: Row(
        children: [
          // Gambar tiket
          ClipRRect(
            borderRadius: BorderRadius.horizontal(left: Radius.circular(8)),
            child: Image.network(
              image,
              width: 100,
              height: 100,
              fit: BoxFit.cover,
            ),
          ),
          SizedBox(width: 8),
          // Detail tiket
          Expanded(
            child: Padding(
              padding: const EdgeInsets.all(8.0),
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  Text(
                    title,
                    style: TextStyle(
                      color: Colors.white,
                      fontWeight: FontWeight.bold,
                      fontSize: 16,
                    ),
                  ),
                  Row(
                    children: [
                      Icon(Icons.warning, color: Colors.red, size: 16),
                      SizedBox(width: 4),
                      Text(
                        age,
                        style: TextStyle(color: Colors.white),
                      ),
                    ],
                  ),
                  SizedBox(height: 4),
                  Text(
                    category,
                    style: TextStyle(color: Colors.grey, fontSize: 12),
                  ),
                  SizedBox(height: 4),
                  Row(
                    children: [
                      Icon(Icons.star, color: Colors.yellow, size: 16),
                      SizedBox(width: 4),
                      Text(
                        rating,
                        style: TextStyle(color: Colors.white),
                      ),
                    ],
                  ),
                  
                ],
              ),
            ),
          ),
        ],
      ),
    );
  }
}
